        console.log(`Generating image for content ID: ${this.contentId}`);

        // Bước 1: Tạo prompt từ nội dung
        const promptResponse = await axios.post(`/api/content/contents/generate-prompt`, {
          contentId: this.contentId,
          style: "Realistic",
