          prompt: generatedPrompt,
          style: "Realistic",
          width: 512,
          height: 512,
          num_inference_steps: 30
        }, {
          timeout: 180000 // 3 minutes timeout for image generation
        });
        if (!imageResponse.data.success) {
          throw new Error(imageResponse.data.error || "Lỗi khi tạo hình ảnh từ mô tả");
