        const imageUrl = `/api/content/contents/${this.contentId}/images/${imageResponse.data.filename}`;

        // Bước 3: Cập nhật URL hình ảnh vào nội dung
        const updateResponse = await axios.put(`/api/content/contents/${this.contentId}`, {
          imageUrl
