    ensureAbsoluteUrl(url) {
      if (!url) return null;

      // If it's already an absolute URL or a data URL, return as is
      if (url.startsWith("http://") || url.startsWith("https://") || url.startsWith("data:")) {
        return url;
      }

      // Special case for server uploads folder
      if (url.includes("uploads/")) {
        // Extract the uploads part and beyond
        const uploadPathMatch = url.match(/(uploads\/.*$)/);
        if (uploadPathMatch && uploadPathMatch[1]) {
          // Handle direct server path to uploads folder
          return `${window.location.origin}/api/image/${uploadPathMatch[1]}`;
        }
      }

      // If it's a relative URL starting with /api, make it absolute
      if (url.startsWith("/api")) {
        // For production, using window.location.origin ensures it works when deployed
        // For development, this will be http://localhost:3001
        return `${window.location.origin}${url}`;
      }

      // If it's a path in server folders but doesn't have /api prefix
      if (url.startsWith("server/") || url.startsWith("/server/")) {
        return `${window.location.origin}/api/${url.replace(/^(\/)?server\//, "")}`;
      }

      // If it doesn't start with / but is still a relative path, add /
      if (!url.startsWith("/")) {
        return `${window.location.origin}/${url}`;
      }

      // Otherwise, assume it's a root-relative URL
      return `${window.location.origin}${url}`;
    },
    handleImageError(event) {
      console.error("Image loading error:", event);
      this.imageError = "Failed to load image";

