    debugImageUrl(originalUrl) {
      console.log("Debugging image URL:", originalUrl);

      // Check various URL formats
      const possibleUrls = [];
      if (originalUrl.includes("uploads/")) {
        // Try different formats for uploads path
        const filename = originalUrl.split("/").pop();

        // Direct server path
        possibleUrls.push(`/api/image/uploads/${filename}`);

        // Full server path with domain
        possibleUrls.push(`${window.location.origin}/api/image/uploads/${filename}`);

        // Server-relative path
        possibleUrls.push(`/server/uploads/${filename}`);
      }

      // Try the original URL with domain
      if (!originalUrl.startsWith("http")) {
        possibleUrls.push(`${window.location.origin}${originalUrl.startsWith("/") ? "" : "/"}${originalUrl}`);
      }
      console.log("Trying alternate URLs:", possibleUrls);

      // Try loading each URL
      possibleUrls.forEach((url, index) => {
        setTimeout(() => {
          const img = new Image();
          img.onload = () => {
            console.log(`Success with URL: ${url}`);
            this.localImage = url;
            this.$emit("update:image", url);
            this.imageError = null;
            this.imageLoading = false;
          };
          img.onerror = () => {
            console.log(`Failed with URL: ${url}`);
          };
          img.src = url;
        }, index * 100);
      });
    },
