import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
import axios from "axios";
export default {
  name: "FinalStep",
  props: {
    content: {
      type: String,
      required: true
    },
    generatedImage: {
      type: String,
      default: null
    },
    projectId: {
      type: String,
      default: ""
    },
    projectTitle: {
      type: String,
      default: "Content Project"
    },
    projectDescription: {
      type: String,
      default: ""
    },
    contentId: {
      type: String,
      default: ""
    },
    fromDashboard: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      projectStatus: "in_progress",
      loading: false,
      error: null,
      saveLoading: false,
      saveMessage: null,
      saveStatus: "success",
      imageLoading: false,
      imageError: null,
      localImage: this.generatedImage,
      toastObj: {
        show: false,
        message: "",
        type: "info",
        // 'info', 'success', 'error'
        timeout: null
      }
    };
  },
  computed: {
    apiEndpoint() {
      return process.env.VUE_APP_API_URL || "http://localhost:3001/api";
    },
    hasProjectId() {
      return this.projectId && this.projectId.length > 0;
    },
    hasContentId() {
      return this.contentId && this.contentId.length > 0;
    },
    formattedContent() {
      if (!this.content) return "";

      // Tạo định dạng HTML cho nội dung
      const formatted = this.content
      // Thay thế dòng trống bằng thẻ đoạn
      .replace(/\n\s*\n/g, "</p><p>")
      // Thay thế dòng mới đơn bằng thẻ br
      .replace(/\n/g, "<br>")
      // Bọc toàn bộ nội dung trong thẻ p
      .replace(/^(.+)$/, "<p>$1</p>");
      return formatted;
    },
    imageUrl() {
      if (this.localImage) {
        if (typeof this.localImage === "string") {
          if (this.localImage.startsWith("data:")) {
            return this.localImage;
          }
          if (this.localImage.startsWith("http") || this.localImage.startsWith("/")) {
            return this.localImage;
          }
        }
        if (this.localImage.image) {
          return this.localImage.image;
        }
      }
      if (this.contentId && !this.localImage) {
        console.log("No image available but has contentId, fetching image");
        this.fetchContentImage();
      }
      return null;
    }
  },
  created() {
    if (this.hasProjectId) {
      this.fetchProjectStatus();
    }
    if (this.hasContentId && !this.generatedImage) {
      this.fetchContentImage();
    }
  },
  methods: {
    async fetchProjectStatus() {
      try {
        if (!this.hasProjectId) return;
        const response = await axios.get(`${this.apiEndpoint}/projects/${this.projectId}`);
        if (response.data.success) {
          this.projectStatus = response.data.data.status;
        }
      } catch (error) {
        console.error("Error fetching project status:", error);
        this.error = "Could not fetch project status";
      }
    },
    async fetchContentImage() {
      if (!this.contentId) return;
      try {
        this.imageLoading = true;
        this.imageError = null;
        console.log(`Fetching image for content ID: ${this.contentId}`);
        const response = await axios.get(`/api/content/contents/${this.contentId}`);
        console.log("Content data received:", response.data);
        if (response.data.success && response.data.data && response.data.data.imageUrl) {
          console.log("Found image URL:", response.data.data.imageUrl);
          this.localImage = response.data.data.imageUrl;
          this.$emit("update:image", response.data.data.imageUrl);
        } else if (response.data.success && response.data.data) {
          console.log("Content found but no image URL available");
        }
      } catch (error) {
        console.error("Error fetching content image:", error);
        this.imageError = error.message;
      } finally {
        this.imageLoading = false;
      }
    },
    async markAsFinished() {
      try {
        if (!this.hasProjectId) {
          alert("Cannot update status: No project ID available");
          return;
        }
        this.loading = true;
        const response = await axios.patch(`${this.apiEndpoint}/projects/${this.projectId}/status`, {
          status: "finished"
        });
        if (response.data.success) {
          this.projectStatus = "finished";
        }
      } catch (error) {
        console.error("Error updating project status:", error);
        alert("Failed to update project status: " + error.message);
      } finally {
        this.loading = false;
      }
    },
    async markAsInProgress() {
      try {
        if (!this.hasProjectId) {
          alert("Cannot update status: No project ID available");
          return;
        }
        this.loading = true;
        const response = await axios.patch(`${this.apiEndpoint}/projects/${this.projectId}/status`, {
          status: "in_progress"
        });
        if (response.data.success) {
          this.projectStatus = "in_progress";
        }
      } catch (error) {
        console.error("Error updating project status:", error);
        alert("Failed to update project status: " + error.message);
      } finally {
        this.loading = false;
      }
    },
    async saveContent() {
      try {
        if (!this.hasContentId) {
          this.saveMessage = "No content ID available. Cannot save.";
          this.saveStatus = "error";
          return;
        }
        this.saveLoading = true;
        this.saveMessage = null;
        const response = await axios.put(`${this.apiEndpoint}/content/contents/${this.contentId}`, {
          generatedContent: this.content
        });
        if (response.data.success) {
          this.saveMessage = "Content saved successfully!";
          this.saveStatus = "success";
        } else {
          throw new Error(response.data.error || "Unknown error occurred");
        }
      } catch (error) {
        console.error("Error saving content:", error);
        this.saveMessage = "Failed to save content: " + error.message;
        this.saveStatus = "error";
      } finally {
        this.saveLoading = false;

        // Clear message after 3 seconds
        setTimeout(() => {
          this.saveMessage = null;
        }, 3000);
      }
    },
    exportContent() {
      try {
        // Tạo dữ liệu để export
        const exportData = {
          title: this.projectTitle || "Untitled Content",
          content: this.content,
          image: this.imageUrl,
          createdAt: new Date().toISOString(),
          projectInfo: {
            id: this.projectId,
            title: this.projectTitle,
            description: this.projectDescription
          }
        };

        // Tạo JSON string
        const dataStr = JSON.stringify(exportData, null, 2);

        // Tạo blob để download
        const blob = new Blob([dataStr], {
          type: "application/json"
        });
        const url = URL.createObjectURL(blob);

        // Tạo link và kích hoạt download
        const a = document.createElement("a");
        a.download = `${this.projectTitle || "content"}_export.json`;
        a.href = url;
        a.click();

        // Giải phóng URL object
        URL.revokeObjectURL(url);
        this.saveMessage = "Content exported successfully!";
        this.saveStatus = "success";

        // Clear message after 3 seconds
        setTimeout(() => {
          this.saveMessage = null;
        }, 3000);
      } catch (error) {
        console.error("Error exporting content:", error);
        this.saveMessage = "Failed to export content: " + error.message;
        this.saveStatus = "error";

        // Clear message after 3 seconds
        setTimeout(() => {
          this.saveMessage = null;
        }, 3000);
      }
    },
    shareContent() {
      // Xử lý chia sẻ nội dung - có thể thông qua email hoặc mạng xã hội
      try {
        // Kiểm tra nếu Web Share API được hỗ trợ
        if (navigator.share) {
          navigator.share({
            title: this.projectTitle || "My Content",
            text: `Check out my content: ${this.projectTitle || "Content"}`,
            url: window.location.href
          }).then(() => {
            this.saveMessage = "Content shared successfully!";
            this.saveStatus = "success";
          }).catch(error => {
            throw error;
          });
        } else {
          // Fallback - copy URL vào clipboard
          navigator.clipboard.writeText(window.location.href).then(() => {
            this.saveMessage = "URL copied to clipboard!";
            this.saveStatus = "success";
          });
        }

        // Clear message after 3 seconds
        setTimeout(() => {
          this.saveMessage = null;
        }, 3000);
      } catch (error) {
        console.error("Error sharing content:", error);
        this.saveMessage = "Failed to share content: " + error.message;
        this.saveStatus = "error";

        // Clear message after 3 seconds
        setTimeout(() => {
          this.saveMessage = null;
        }, 3000);
      }
    },
    downloadPDF() {
      try {
        // Thông báo cho người dùng rằng chức năng đang được phát triển
        this.saveMessage = "PDF download feature will be available soon.";
        this.saveStatus = "success";

        // Xóa thông báo sau 3 giây
        setTimeout(() => {
          this.saveMessage = null;
        }, 3000);

        // NOTE: Chức năng này sẽ được triển khai trong bản cập nhật tới
        // Sử dụng thư viện như jsPDF hoặc html2pdf.js để tạo PDF từ nội dung
      } catch (error) {
        console.error("Error downloading PDF:", error);
        this.saveMessage = "Failed to download PDF: " + error.message;
        this.saveStatus = "error";

        // Clear message after 3 seconds
        setTimeout(() => {
          this.saveMessage = null;
        }, 3000);
      }
    },
    goToDashboard() {
      this.$emit("go-to-dashboard");
    },
    async generateImageForContent() {
      if (!this.contentId) return;
      try {
        this.imageLoading = true;
        this.imageError = null;
        console.log(`Generating image for content ID: ${this.contentId}`);

        // Timeout 3 phút (180000ms)
        const controller = new AbortController();
        const timeoutId = setTimeout(() => controller.abort(), 180000);
        try {
          const response = await fetch(`/api/content/contents/${this.contentId}/generate-image`, {
            method: "POST",
            headers: {
              "Content-Type": "application/json"
            },
            body: JSON.stringify({
              style: "Realistic"
            }),
            signal: controller.signal
          });

          // Xoá timeout nếu request thành công
          clearTimeout(timeoutId);
          if (!response.ok) {
            throw new Error(`Error generating image: ${response.statusText}`);
          }
          const data = await response.json();
          console.log("Image generation response:", data);
          if (data.success && data.data && data.data.imageUrl) {
            console.log("New image URL:", data.data.imageUrl);
            this.localImage = data.data.imageUrl;
            this.$emit("update:image", data.data.imageUrl);
            this.showToast("Image generated successfully", "success");
          } else {
            throw new Error("Image generation did not return a valid URL");
          }
        } catch (requestError) {
          clearTimeout(timeoutId);
          if (requestError.name === "AbortError") {
            throw new Error("Request timeout: Image generation is taking too long");
          }
          throw requestError;
        }
      } catch (error) {
        console.error("Error generating image for content:", error);
        this.imageError = error.message;

        // Kiểm tra nếu lỗi liên quan đến FastAPI service
        if (error.message.includes("Failed to fetch") || error.message.includes("NetworkError") || error.message.includes("timeout") || error.message.includes("ECONNREFUSED")) {
          this.showToast("Error connecting to image generation service. Please make sure the service is running at http://localhost:8000", "error");
        } else {
          this.showToast(`Error generating image: ${error.message}`, "error");
        }
      } finally {
        this.imageLoading = false;
      }
    },
    showToast(message, type = "info") {
      // Clear any existing timeout
      if (this.toastObj.timeout) {
        clearTimeout(this.toastObj.timeout);
      }

      // Set toast properties
      this.toastObj.show = true;
      this.toastObj.message = message;
      this.toastObj.type = type;

      // Auto hide after 5 seconds
      this.toastObj.timeout = setTimeout(() => {
        this.toastObj.show = false;
      }, 5000);

      // Log for debugging
      console.log(`Toast: ${type} - ${message}`);
    }
  },
  watch: {
    generatedImage: {
      immediate: true,
      handler(newValue) {
        if (newValue) {
          console.log("New generatedImage received via props:", newValue);
          this.localImage = newValue;
        }
      }
    },
    contentId: {
      immediate: true,
      handler(newValue) {
        if (newValue && !this.localImage) {
          console.log("Content ID changed, fetching image for:", newValue);
          this.fetchContentImage();
        }
      }
    }
  }
};
